mapper.insert:
INSERT INTO NOTICE
(
	NOTICE_SEQ, TITLE, CONTENTS, USER_ID, READ_CNT,
	REG_ID, REG_DT, CHG_ID, CHG_DT, SAVE_FILE_NAME, SAVE_FILE_PATH, ORG_FILE_NAME, EXT
)
VALUES
	(
			(SELECT IFNULL(MAX(A.NOTICE_SEQ), 0) + 1 FROM NOTICE A),
			#{title}, #{contents}, #{userId}, 0, #{userId}, NOW(), #{userId}, NOW(), #{fileName}, #{filePath}, #{orgFileName}, #{ext}
	)
